import csvwith open('persons_info.csv', 'r') as fr:    fr_reader = csv.DictReader(fr)    """    for line in fr_reader:        print(line['email'])    """    with open('persons_names_dict.csv', 'w') as fw:        # fieldnames = ['first_name','last_name','email']        fieldnames = ['first_name','last_name']        fw_writer = csv.DictWriter(fw,fieldnames=fieldnames,delimiter='\t')        fw_writer.writeheader()        for line in fr_reader:            del line['email']            fw_writer.writerow(line)